import React from "react";
import "./App.css";

class FileInputOrImage extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fileInput = React.createRef();
    this.canvas = React.createRef();
  }
  handleSubmit(event) {
    event.preventDefault();
    const imgUrl = URL.createObjectURL(this.fileInput.current.files[0]);
    this.setState({
      localFileUrl: imgUrl,
    });
  }

  componentDidMount() {
    this.ctx = this.canvas.current.getContext("2d");
    window.context = this.ctx
  }

  componentDidUpdate() {
    if (this.state && this.state.localFileUrl) {
      const DOMImage = new Image(640, 425)
      DOMImage.src = this.state.localFileUrl
      DOMImage.onload = () =>  {this.ctx.drawImage(DOMImage, 0, 0)}
    }
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          <p>{this.state ? "Nice pic" : "Upload a file of your wall"}</p>
          <input type="file" ref={this.fileInput} />
        </label>
        <br />
        <button type="submit">Submit</button>
          <canvas ref={this.canvas} width={640} height={425} />
      </form>
    );
  }
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <FileInputOrImage />
      </header>
    </div>
  );
}

export default App;
