import React from 'react';
import logo from './logo.svg';
import './App.css';


class FileInputOrImage extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fileInput = React.createRef();
  }
  handleSubmit(event) {
    event.preventDefault();
    this.setState({localFileUrl: URL.createObjectURL(this.fileInput.current.files[0])})
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          <p>{this.state ? "Nice pic" : "Upload a file of your wall"}</p>
          <input type="file" ref={this.fileInput} />
          <img src={this.state ? this.state.localFileUrl : ""} alt=""/>
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    );
  }
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <FileInputOrImage/>
      </header>
    </div>
  );
}

export default App;
