import React from "react";
import "./App.css";
import interact from 'interactjs';

const numLeds = 5;

function Led({ledX, ledY}) {
  return <svg height="100" width="100" className="led">
  <circle cx={ledX} cy={ledY} r="10" stroke="white" stroke-width="1" fill="rgba(255, 0, 0, 0.8)" />
</svg>
}

class FileInputOrImage extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fileInput = React.createRef();
    this.ledX = 50
    this.ledY = 50

  }
  handleSubmit(event) {
    event.preventDefault();
    const imgUrl = URL.createObjectURL(this.fileInput.current.files[0]);
    this.setState({
      localFileUrl: imgUrl,
    });
  }

  componentDidMount() {
    window.context = this.ctx

interact('.led').draggable({
  listeners: {
    start (event) {
      console.log(event.type, event.target)
    },
    move (event) {
      this.setState({ledX: this.state.ledX + event.dx})
      this.setState({ledX: this.state.ledY + event.dy})
    },
  }
})
  }

  componentDidUpdate() {
    if (this.state && this.state.localFileUrl) {
      const DOMImage = new Image(640, 425)
      DOMImage.src = this.state.localFileUrl

    }
  }

  render() {
    return (
      <div>
      <form onSubmit={this.handleSubmit}>
        <label>
          <p>{this.state ? "Nice pic" : "Upload a file of your wall"}</p>
          <input type="file" ref={this.fileInput} />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
      <img width={1280} height={650} src={this.state ? this.state.localFileUrl : ""} alt="no image found" />
        <Led {...this.state}/>
      </div>
    );
  }
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <FileInputOrImage />
      </header>
    </div>
  );
}

export default App;
