import React from "react";
import "./App.css";

const numLeds = 5;

function Led() {
  return <svg height={150} width={500} className="led"> 
  <defs>
    <radialGradient id={"grad1"} cx={"50%"} cy={"50%"} r={"50%"} fx={"50%"} fy={"50%"}>
      <stop offset={"0%"} style={{stopColor:"rgb(255,255,255)", stopOpacity: 0}} />
      <stop offsetA={"100%"} style={{stopColor: "rgb(0,0,255)", stopOpacity: 1}} />
    </radialGradient>
  </defs>
  <ellipse cx={200} cy={70} rx={85} ry={55} fill={"url(#grad1)"} />
</svg>
}

class FileInputOrImage extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fileInput = React.createRef();
    this.canvas = React.createRef();
  }
  handleSubmit(event) {
    event.preventDefault();
    const imgUrl = URL.createObjectURL(this.fileInput.current.files[0]);
    this.setState({
      localFileUrl: imgUrl,
    });
  }

  componentDidMount() {
    this.ctx = this.canvas.current.getContext("2d");
    window.context = this.ctx
  }

  componentDidUpdate() {
    if (this.state && this.state.localFileUrl) {
      const DOMImage = new Image(640, 425)
      DOMImage.src = this.state.localFileUrl
      DOMImage.onload = () =>  {
        this.ctx.drawImage(DOMImage, 0, 0, 640, 425);
        var gradient = this.ctx.createLinearGradient(0, 0, 30, 30)
        gradient.addColorStop(0, 'green');
        gradient.addColorStop(.5, 'cyan');
        gradient.addColorStop(1, 'green');
        this.ctx.fillStyle = gradient

        this.ctx.strokeStyle = "rgba(255, 255, 255, 0.3)"
        this.ctx.fillRect(0, 0, 30, 30);
      }

    }
  }

  render() {
    return (
      <div>
      <form onSubmit={this.handleSubmit}>
        <label>
          <p>{this.state ? "Nice pic" : "Upload a file of your wall"}</p>
          <input type="file" ref={this.fileInput} />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
      <canvas ref={this.canvas} width={640} height={425} />
      </div>
    );
  }
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <FileInputOrImage />
      </header>
    </div>
  );
}

export default App;
