import React from "react";
import "./App.css";

class FileInputOrImage extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fileInput = React.createRef();
  }
  handleSubmit(event) {
    event.preventDefault();
    const imgUrl = URL.createObjectURL(this.fileInput.current.files[0]);
    const image = new Image()
    image.src = imgUrl
    this.setState({
      localFileUrl: imgUrl,
      image: image
    });
  }

  componentDidMount() {
    this.ctx = this.refs.canvas.getContext("2d");
  }

  componentDidUpdate() {
    if (this.state && this.state.localFileUrl) {
      this.ctx.drawImage(this.state.image, 0, 0);
    }
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          <p>{this.state ? "Nice pic" : "Upload a file of your wall"}</p>
          <input type="file" ref={this.fileInput} />
          <canvas ref="canvas" width={640} height={425} />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    );
  }
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <FileInputOrImage />
      </header>
    </div>
  );
}

export default App;
